import React, { useState } from 'react';

function Cart() {
  const [cartItems, setCartItems] = useState([]); // Cart content stored here
  const [cartName, setCartName] = useState('');
  const [loadCartName, setLoadCartName] = useState('');

  const handleAddToCart = () => {
    console.log("Adding item to cart")
    const newItem = `Item ${cartItems.length + 1}`;
    setCartItems([...cartItems, newItem]);
  };

  const handleBuy = () => {
    console.log('Buying items');
  };

  const handleClear = () => {
    console.log('Clearing cart');
    setCartItems([]);
  };

  const handleSaveCart = () => {
    console.log(`Saving cart with name: ${cartName}`);
  };

  const handleLoadCart = () => {
    console.log(`Loading cart with name: ${loadCartName}`);
  };

  return (
    <div
      style={{
        position: 'absolute',
        bottom: '10px',
        right: '10px',
        padding: '20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
        width: '350px',
      }}
    >
        <div style={{ width: '50%' }}>
      <button
        onClick={handleAddToCart}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          borderRadius: '5px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          marginBottom: '10px',
          width: '100%',
        }}
      >
        Add to Cart
      </button>
      <button
        onClick={handleBuy}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          borderRadius: '5px',
          backgroundColor: '#2196F3',
          color: 'white',
          border: 'none',
          marginBottom: '10px',
          width: '100%',
        }}
      >
        Buy
      </button>
      <button
        onClick={handleClear}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          borderRadius: '5px',
          backgroundColor: '#f44336',
          color: 'white',
          border: 'none',
          marginBottom: '10px',
          width: '100%',
        }}
      >
        Clear
      </button>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '10px',
        }}
      >
        <input
          type="text"
          placeholder="Cart Name"
          value={cartName}
          onChange={(e) => setCartName(e.target.value)}
          style={{
            padding: '8px',
            fontSize: '14px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            flex: 1,
            marginRight: '10px',
          }}
        />
        <button
          onClick={handleSaveCart}
          style={{
            padding: '8px 12px',
            fontSize: '14px',
            cursor: 'pointer',
            borderRadius: '5px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
          }}
        >
          Save
        </button>
      </div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        <input
          type="text"
          placeholder="Load Cart"
          value={loadCartName}
          onChange={(e) => setLoadCartName(e.target.value)}
          style={{
            padding: '8px',
            fontSize: '14px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            flex: 1,
            marginRight: '10px',
          }}
        />
        <button
          onClick={handleLoadCart}
          style={{
            padding: '8px 12px',
            fontSize: '14px',
            cursor: 'pointer',
            borderRadius: '5px',
            backgroundColor: '#2196F3',
            color: 'white',
            border: 'none',
          }}
        >
          Load
        </button>
        </div>
      </div>
    </div>
  );
}

export default Cart;
