import './App.css';
import React, { useState } from 'react';

function App() {
  // State to hold the value from the text box
  const [inputValue, setInputValue] = useState('');

  // Function to handle button click
  const handleSubmit = () => {
    console.log('Submitted value:', inputValue);
    alert(`You entered: ${inputValue}`);
  };

  return (
    <div>
      <header className="App-header">
        <p>Gifto</p>

        {/* Input Text Box */}
        <input
          type="text"
          placeholder="Enter something..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          style={{
            padding: '8px',
            fontSize: '16px',
            marginBottom: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
          }}
        />

        {/* Submit Button */}
        <button
          onClick={handleSubmit}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            cursor: 'pointer',
            borderRadius: '5px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
          }}
        >
          Submit
        </button>
      </header>
    </div>
  );
}

export default App;
