import React, { useState } from 'react';

function FindUsers() {
  const [usernameToFind, setUsernameToFind] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Function to fetch matching usernames
  const fetchSuggestions = async (query) => {
    if (!query) {
      setSuggestions([]); // Clear suggestions if input is empty
      return;
    }
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:5000/find_user_autocomplete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query }),
      });
      const result = await response.json();
      setSuggestions(result.usernames || []); // Assume API returns { usernames: [] }
    } catch (error) {
      console.error('Error fetching suggestions:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Debounce function to limit API calls
  const debounce = (func, delay) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => func(...args), delay);
    };
  };

  // Debounced fetchSuggestions
  const debouncedFetchSuggestions = debounce(fetchSuggestions, 300);

  // Handle input change
  const handleInputChange = (e) => {
    const value = e.target.value;
    setUsernameToFind(value);
    debouncedFetchSuggestions(value);
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: '10px',
        left: '10px',
        padding: '20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
        width: '250px',
      }}
    >
      <input
        type="text"
        placeholder="Type username"
        value={usernameToFind}
        onChange={handleInputChange}
        style={{
          padding: '8px',
          fontSize: '16px',
          marginBottom: '10px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          width: '100%',
        }}
      />
      {isLoading && <p>Loading...</p>}
      {suggestions.length > 0 && (
        <ul
          style={{
            listStyleType: 'none',
            padding: 0,
            margin: 0,
            border: '1px solid #ccc',
            borderRadius: '5px',
            backgroundColor: '#fff',
            maxHeight: '150px',
            overflowY: 'auto',
          }}
        >
          {suggestions.map((username, index) => (
            <li
              key={index}
              style={{
                padding: '8px',
                cursor: 'pointer',
                borderBottom: '1px solid #eee',
              }}
              onClick={() => {
                setUsernameToFind(username);
                setSuggestions([]); // Clear suggestions on selection
              }}
            >
              {username}
            </li>
          ))}
        </ul>
      )}
      <button
        onClick={() => console.log(`Selected Username: ${usernameToFind}`)}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          borderRadius: '5px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          marginTop: '10px',
          width: '100%',
        }}
      >
        Find User
      </button>
    </div>
  );
}

export default FindUsers;
