import React, { useState } from 'react';

function FindUsers() {
  const [usernameToFind, setUsernameToFind] = useState('');

  const handleFindUser = async () => {
    try {
      const response = await fetch('http://localhost:5000/find_user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username: usernameToFind }),
      });
      const result = await response.json();
      console.log('Find User Response:', result);
      alert(result.response || 'User not found'); // Display the result
    } catch (error) {
      console.error('Error during user search:', error);
    }
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: '10px', // Top-left corner
        left: '10px',
        padding: '20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
      }}
    >
      <input
        type="text"
        placeholder="Type username"
        value={usernameToFind}
        onChange={(e) => setUsernameToFind(e.target.value)}
        style={{
          padding: '8px',
          fontSize: '16px',
          marginBottom: '10px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          width: '100%',
        }}
      />
      <button
        onClick={handleFindUser}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          borderRadius: '5px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          width: '100%',
        }}
      >
        Find User
      </button>
    </div>
  );
}

export default FindUsers;
