{/* Login Box */}
<div
  style={{
    position: 'absolute',
    top: '10px',
    right: '10px', // Move to the top-right
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    width: '300px',
  }}
>
  <button
    onClick={handleLogin}
    style={{
      padding: '10px 20px',
      fontSize: '16px',
      cursor: 'pointer',
      borderRadius: '5px',
      backgroundColor: '#4CAF50',
      color: 'white',
      border: 'none',
      width: '100%',
    }}
  >
    Login
  </button>
</div>

{/* Registration Box */}
<div
  style={{
    position: 'absolute',
    top: '100px', // Adjusted position to be below the login box
    right: '10px', // Move to the top-right
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    width: '300px',
  }}
>
  <div style={{ marginBottom: '10px' }}>
    <label style={{ marginRight: '10px', fontWeight: 'bold' }}>Username:</label>
    <input
      type="text"
      value={localUsername}
      onChange={(e) => setLocalUsername(e.target.value)}
      style={{
        padding: '8px',
        fontSize: '14px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '60%',
      }}
    />
  </div>
  <div style={{ marginBottom: '10px' }}>
    <label style={{ marginRight: '10px', fontWeight: 'bold' }}>Password:</label>
    <input
      type="password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
      style={{
        padding: '8px',
        fontSize: '14px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '60%',
      }}
    />
  </div>
  <div style={{ marginBottom: '10px' }}>
    <label style={{ marginRight: '10px', fontWeight: 'bold' }}>Email:</label>
    <input
      type="email"
      value={email}
      onChange={(e) => setEmail(e.target.value)}
      style={{
        padding: '8px',
        fontSize: '14px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '60%',
      }}
    />
  </div>
  <div style={{ marginBottom: '10px' }}>
    <label style={{ marginRight: '10px', fontWeight: 'bold' }}>Phone:</label>
    <input
      type="tel"
      value={phone}
      onChange={(e) => setPhone(e.target.value)}
      style={{
        padding: '8px',
        fontSize: '14px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '60%',
      }}
    />
  </div>
  <div style={{ marginBottom: '10px' }}>
    <label style={{ marginRight: '10px', fontWeight: 'bold' }}>Notifications:</label>
    <select
      value={notifications}
      onChange={(e) => setNotifications(e.target.value === 'true')}
      style={{
        padding: '8px',
        fontSize: '14px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '65%',
      }}
    >
      <option value="false">No</option>
      <option value="true">Yes</option>
    </select>
  </div>
  <button
    onClick={handleRegister}
    style={{
      padding: '10px 20px',
      fontSize: '16px',
      cursor: 'pointer',
      borderRadius: '5px',
      backgroundColor: '#2196F3',
      color: 'white',
      border: 'none',
      width: '100%',
    }}
  >
    Register
  </button>
</div>
