import React, { useState } from 'react';
import TextInput from './components/TextInput';
import ImageInput from './components/ImageInput';
import DropdownSelection from './components/DropdownSelection';

function Home() {
  const sendInputData = async (inputValue) => {
    console.log('Submitted value:', inputValue);
    try {
        const response = await fetch('http://localhost:5000/product_text', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ inputValue }),
        });
  
        const result = await response.json();
        console.log('Response from backend:', result);
      } catch (error) {
        console.error('Error sending input data:', error);
      }
    };

  const uploadImage = async (file) => {
    console.log('Uploading file:', file);
    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await fetch('http://localhost:5000/product_img', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      console.log('Response from backend:', result);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  const handleSubmitSelection = (selectedOption) => {
    console.log('Selected option:', selectedOption);
  };

  return (
    <div>
      <header className="App-header">
        <p>Gifto</p>
        <TextInput sendInputData={sendInputData} />
        <DropdownSelection handleSubmit={handleSubmitSelection} />
        <ImageInput uploadImage={uploadImage} />
      </header>
    </div>
  );
}

export default Home;
