import React, { useState, useEffect } from 'react';

function Cart({ username }) {
  const [cartItems, setCartItems] = useState([]); // Cart content stored here
  const [cartName, setCartName] = useState('');
  const [loadCartName, setLoadCartName] = useState('');
  const [deleteCartName, setDeleteCartName] = useState('');
  const [totalCost, setTotalCost] = useState(0); // Total cart cost

  // Recalculate total cost whenever cart items or quantities change
  useEffect(() => {
    const newTotalCost = cartItems.reduce(
      (acc, item) => acc + item.quantity * item.pricePerUnit,
      0
    );
    setTotalCost(newTotalCost);
  }, [cartItems]);

  const handleAddToCart = () => {
    const newItem = {
      name: `Basketball`,
      quantity: 1,
      pricePerUnit: 10,
      supplier:
        'https://www.amazon.co.uk/AmazonBasics-PU-Composite-Basketball-Official/dp/B07VL3NHMY',
    };
    setCartItems([...cartItems, newItem]);
  };

  const handleClear = () => {
    setCartItems([]);
  };

  const handleRemoveItem = (index) => {
    const updatedCart = cartItems.filter((_, i) => i !== index);
    setCartItems(updatedCart);
  };

  const handleQuantityChange = (index, quantity) => {
    const updatedCart = cartItems.map((item, i) =>
      i === index ? { ...item, quantity } : item
    );
    setCartItems(updatedCart);
  };

  return (
    <div
      style={{
        position: 'absolute',
        bottom: '10px',
        right: '10px',
        padding: '20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
        width: '550px',
        height: '300px',
        display: 'flex',
        flexDirection: 'row', // Align cart and buttons side by side
      }}
    >
      {/* Cart Content */}
      <div
        style={{
          width: '60%',
          marginRight: '10px',
          padding: '10px',
          border: '1px solid #ccc',
          borderRadius: '5px',
          backgroundColor: '#fff',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          color: 'black',
        }}
      >
        <p
          style={{
            fontSize: '16px',
            fontWeight: 'bold',
            marginBottom: '10px',
            color: 'black',
          }}
        >
          Cart
        </p>
        <ul
          style={{
            listStyleType: 'none',
            padding: 0,
            margin: 0,
            maxHeight: '200px',
            overflowY: 'auto',
          }}
        >
          {cartItems.length > 0 ? (
            cartItems.map((item, index) => (
              <li
                key={index}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  padding: '8px',
                  fontSize: '14px',
                  borderBottom:
                    index !== cartItems.length - 1
                      ? '1px solid #eee'
                      : 'none',
                }}
              >
                <input type="checkbox" style={{ marginRight: '10px' }} />
                {/* Clickable name that opens the supplier link */}
                <a
                  href={item.supplier}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    textDecoration: 'none',
                    color: '#007BFF', // Blue text for link
                    cursor: 'pointer',
                  }}
                >
                  {item.name}
                </a>
                <select
                  value={item.quantity}
                  onChange={(e) =>
                    handleQuantityChange(index, parseInt(e.target.value, 10))
                  }
                  style={{
                    marginLeft: '10px',
                    marginRight: '10px',
                    borderRadius: '5px',
                    padding: '5px',
                  }}
                >
                  {[1, 2, 3, 4, 5].map((qty) => (
                    <option key={qty} value={qty}>
                      {qty}
                    </option>
                  ))}
                </select>
                <p
                  style={{
                    marginLeft: '10px',
                    marginRight: '10px',
                    fontSize: '14px',
                    color: '#333',
                  }}
                >
                  £{(item.quantity * item.pricePerUnit).toFixed(2)}
                </p>
                <button
                  onClick={() => handleRemoveItem(index)}
                  style={{
                    padding: '5px',
                    fontSize: '12px',
                    cursor: 'pointer',
                    borderRadius: '5px',
                    backgroundColor: '#f44336',
                    color: 'white',
                    border: 'none',
                  }}
                >
                  X
                </button>
              </li>
            ))
          ) : (
            <li style={{ fontSize: '14px', color: '#666' }}>
              Your cart is empty
            </li>
          )}
        </ul>
        {/* Total Cart Cost */}
        <div
          style={{
            textAlign: 'right',
            marginTop: '10px',
            fontWeight: 'bold',
            fontSize: '16px',
          }}
        >
          Total: £{totalCost.toFixed(2)}
        </div>
      </div>

      {/* Buttons and Inputs */}
      <div style={{ width: '40%' }}>
        <button
          onClick={handleAddToCart}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            cursor: 'pointer',
            borderRadius: '5px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            marginBottom: '10px',
            width: '100%',
          }}
        >
          Add to Cart
        </button>
        <button
          onClick={() => handleBuy()}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            cursor: 'pointer',
            borderRadius: '5px',
            backgroundColor: '#2196F3',
            color: 'white',
            border: 'none',
            marginBottom: '10px',
            width: '100%',
          }}
        >
          Buy
        </button>
        <button
          onClick={handleClear}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            cursor: 'pointer',
            borderRadius: '5px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            marginBottom: '10px',
            width: '100%',
          }}
        >
          Clear
        </button>
      </div>
    </div>
  );
}

export default Cart;
