import React, { useState } from 'react';
import Select from 'react-select';

// Grouped options data
const groupedOptions = [
  {
    label: 'Personal Occasions',
    options: [
      { value: 'birthday', label: 'Birthday' },
      { value: 'anniversary', label: 'Anniversary' },
      { value: 'graduation', label: 'Graduation' },
    ],
  },
  {
    label: 'Festive Occasions',
    options: [
      { value: 'christmas', label: 'Christmas' },
      { value: 'eid', label: 'Eid' },
      { value: 'diwali', label: 'Diwali' },
    ],
  },
  {
    label: 'Professional Occasions',
    options: [
      { value: 'promotion', label: 'Promotion' },
      { value: 'farewell', label: 'Farewell' },
    ],
  },
];

const handleSubmitSelection = (selectedOption) => {
  if (selectedOption) {
    console.log('Selected Option:', selectedOption);
    alert(`You selected: ${selectedOption.label}`);
  } else {
    alert('Please select an option.');
  }
}

function DropdownSelection() {
  const [selectedOption, setSelectedOption] = useState(null);

  // Handle selection change
  const handleChange = (option) => {
    setSelectedOption(option);
  };

  return (
    <div style={{ width: '300px', marginBottom: '20px' }}>
      <Select
        options={groupedOptions} // Pass grouped options here
        value={selectedOption} // Selected value
        onChange={handleChange} // Handle value change
        placeholder="Select an Occasion"
        isSearchable // Optional: makes the dropdown searchable
      />

      {/* Submit Button */}
      <button
        onClick={() => handleSubmitSelection(selectedOption)}
        style={{
          marginTop: '10px',
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          borderRadius: '5px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
        }}
      >
        Submit Selection
      </button>
    </div>
  );
}

export default DropdownSelection;
